mapper.select:
SELECT i.hotel_no, i.hotel_nm, i.hotel_nm_kr, i.nat, i.state, i.state_kr, i.country, i.hotel_adr, i.hotel_tel, i.hotel_url, MIN(r.price1) as price1, r.breakfast_yn, d.room_cnt, d.grade, d.resortfee_yn FROM fit_hotel_info i, fit_room_type r, fit_hotel_detail d WHERE i.hotel_no = r.hotel_no AND i.hotel_no = d.hotel_no
AND i.hotel_no = #{hotelNo}
AND i.type_no = #{typeNo}
GROUP BY i.hotel_no

mapper.insert:
INSERT INTO fit_hotel_cate (cate_no, hotel_no, cate_nm, duration1, duration2, duration3, duration4, remark, cancel_policy) VALUES (
#{item.cateNo}, #{item.hotelNo}, #{item.cateNm}, #{item.duration1}, #{item.duration2}, #{item.duration3}, #{item.duration4}, #{item.remark}, #{item.cancelPolicy}
)

INSERT INTO fit_room_type (type_no, hotel_no, cate_no, type_nm, agency_cd, price1, price2, price3, price4, breakfast_yn, breakfast_price, cot_price, cradle_yn, ac_yn, fridge_yn, bath_yn, garden_yn, vault_yn) VALUES (
#{item.typeNo}, #{item.hotelNo}, #{item.cateNo}, #{item.typeNm}, #{item.agencyCd}, #{item.price1}, #{item.price2}, #{item.price3}, #{item.price4}, #{item.breakfastYn}, #{item.breakfastPrice}, #{item.cotPrice}, #{item.cradleYn}, #{item.acYn}, #{item.fridgeYn}, #{item.bathYn}, #{item.gardenYn}, #{item.vaultYn}
)
